SELECT DISTINCT flight_1.flight_id FROM flight flight_1 , airport_service airport_service_1 ON flight_1.from_airport = airport_service_1.airport_code , city city_1 ON airport_service_1.city_code = city_1.city_name , airport_service airport_service_2 ON flight_1.to_airport = airport_service_2.airport_code , city city_2 ON airport_service_2.city_code = city_2.city_name WHERE flight_1.departure_time BETWEEN 0 AND 1800 AND city_1.city_name = 'JFK' AND city_2.city_name = 'MIAMI'
